</template>

<script>
import Carousel from "./common/carousel";
import AppFooter from './common/app-footer';

export default {
    name: "home",
    components: {
        Carousel, AppFooter
    },
    data() {
        return {
            cardMenu: [
                {
                    name: "Dmitry Kiselev",
                    position: "(Project Manager)",
                    img: "data:image/jpeg;base64,"
                },
                {
                    name: "Korchagin Ivan",
                    position: "(IOS Developer)",
                    img: "data:image/jpeg;base64,"
                },
                {
                    name: "Valevko Alisa",
                    position: "(C++ Developer)",
                    img: "data:image/jpeg;base64,"
                },
                {
                    name: "Brazhnik Maxim",
                    position: "(.NET Developer)",
                    img: "data:image/jpeg;base64,"
                },
                {
                    name: "Vasilenko Yaroslav",
                    position: "(Frontend Developer)",
                    img: "data:image/jpeg;base64,"
                },
                {
                    name: "Porik Sergey",
                    position: "(Web designer)",
                    img: "data:image/jpeg;base64,"
                }
            ],
            cardPerRow: 3
        }
    },
    computed: {
        rows() {
            return (new Array(Math.floor(this.cardMenu.length / this.cardPerRow)));
        }
    },
    methods: {
        getCardItems(id) {
            return this.cardMenu.slice(id * this.cardPerRow, (id + 1) * this.cardPerRow);
        }
    }
}
</script>

